    public static List<Previsione> listaPrevisioni = new ArrayList<>();

    public static String parse() {

        listaPrevisioni.clear();

        try {
            JSONObject all = new JSONObject(jsonString);
            String nomecitta = all.getJSONObject("city").getString("name");

            JSONArray lista = all.getJSONArray("list");

            for (int i = 0; i < lista.length(); i++) {
                JSONObject el = lista.getJSONObject(i);
                double temp = el.getJSONObject("main").getDouble("temp");
                double umidita = el.getJSONObject("main").getDouble("humidity");
                String cielo = el.getJSONArray("weather").getJSONObject(0).
                        getString("main");
                String dt_txt = el.getString("dt_txt");
                String data = dt_txt.substring(0, dt_txt.indexOf(' '));
                String ora = dt_txt.substring(dt_txt.indexOf(' '));
                int iCielo = 0;
                switch (cielo) {
                    case "Clear":
                        iCielo = 0;
                        break;
                    case "Clouds":
                        iCielo = 1;
                        break;
                    case "Rain":
                        iCielo = 2;
                        break;

                }

                listaPrevisioni.add(new Previsione(data, ora, nomecitta, temp,
                        umidita, iCielo));
            }
            Log.i("JSON", nomecitta);
            Log.i("JSON", "Numero previsioni "+ listaPrevisioni.size());
            return nomecitta;
        } catch (JSONException e) {
            Log.i("JSON", e.getLocalizedMessage());
            return e.getLocalizedMessage();
        }

    }


